.goods_item {
    &.sold_out, &.coming_soon {
        .img_wrap:before, .img_wrap:after {
            content:'';
            display:block;
        }
        .img_wrap:before {
            background-color:rgba(0,0,0,0.3);
            background-position:50%;
            background-repeat:no-repeat;
            position:absolute;
            top:0;
            right:0;
            bottom:0;
            left:0;
            z-index:1;
        }
        &.sold_out {
            .img_wrap:before {
                background-image:url('/_nuxt/assets/images/ui/overlay_soldout.png');
            }
        }
        &.coming_soon {
            .img_wrap:before {
                background-image:url('/_nuxt/assets/images/ui/overlay_comingsoon.png');
            }
        }
    }
    .img_wrap {
        position:relative;
        overflow:hidden;
        .thumb {
            height:320px;
            position:relative;
            display:block;
            em {
                position:absolute;
                top:0;
                right:0;
                bottom:0;
                left:0;
                display:flex;
                align-items:center;
                img {
                    width:100%;
                    height:100%;
                    display:block;
                    object-fit:cover;
                }
                &.overflip {
                    opacity:0;
                    top:50%;
                    bottom:50%;
                    transition:all 0.3s ease-out;
                }
            }
        }
        .btnIconBox {
            background-color:#00BC70;
            position:absolute;
            right:0;
            bottom:0;
            left:0;
            z-index:1;
            display:flex;
            justify-content:space-around;
            transform:translateY(100%);
            transition:transform 0.3s ease-out;
            button {
                height:auto;
                padding:14px 5px;
                font-size:0;
                background-color:transparent;
                em {
                    width:32px;
                    height:32px;
                    padding:0;
                    background-image:url('/_nuxt/assets/images/common/icon_split.png');
                    background-repeat:no-repeat;
                }
                &.btn_heart {
                    em {
                        background-position:-220px 0;
                    }
                }
                &.btn_cart {
                    em {
                        background-position:-280px 0;
                    }
                }
                &.btn_buy {
                    em {
                        background-position:-340px 0;
                    }
                }
            }
        }
        &:hover {
            .overflip {
                opacity:1 !important;
                top:0 !important;
                right:0 !important;
                bottom:0 !important;
                left:0 !important;
            }
            .btnIconBox {
                transform:translateY(0);
            }
        }
    }
    .review_score {
        color:#999e9c;
        font-size:12px;
        display:flex;
        align-items:center;
        &:before {
            width:12px;
            height:12px;
            margin-right:2px;
            background-image:url('/_nuxt/assets/images/common/icon_split.png');
            background-position:-400px 0;
            background-repeat:no-repeat;
            content:'';
            display:block;
        }
    }    
}
