/* #wrap.mo : 모바일 스타일 */

.lockbody {
    overflow:hidden;
}

.docTopBanner {
    height:50px;
    background-color:#000;
    display:flex;
    align-items:center;
    justify-content:center;
    .txt {
        padding:0 20px;
        color:#fff;
        font-size:14px;
        display:block;
    }
    .btnClose {
        width:16px;
        height:16px;
        margin-left:10px;
        font-size:0;
        position:relative;
        &:before, &:after {
            width:13px;
            margin-left:2px;
            border-top:1px solid #fff;
            content:'';
            position:absolute;
            top:50%;
            left:0;
            display:block;
        }
        &:before {
            transform:rotate(45deg);
        }
        &:after {
            transform:rotate(-45deg);
        }
    }
}

#wrap.MO {
    .docTopBanner {
        padding-right:21px;
        justify-content:space-between;
    }
}

.form_group {
    > li + li {
        margin-top:20px;
    }
    .form_tit {
        margin-bottom:10px;
        font-size:14px;
    }
}

.multi_form {
    display:flex;
    gap:10px;
    flex-wrap:wrap;

}

/* modal */
.modal_wrap {
    position:fixed;
    top:0;
    right:0;
    bottom:0;
    left:0;
    z-index:10;
    display:none;
    &.active {
        display:block;
    }
    .overlay {
        background-color:rgba(0,0,0,0.7);
        position:fixed;
        top:0;
        right:0;
        bottom:0;
        left:0;
    }
    .modal_container {
        max-height:calc(100% - 40px);
        background-color:#fff;
        overflow:hidden;
        position:absolute;
        top:50%;
        left:50%;
        z-index:1;
        display:flex;
        flex-direction:column;
        transform:translate(-50%, -50%);
        .modal_header {
            padding:20px;
            border-bottom:1px solid #eee;
            .btn_close {
                width:24px;
                height:24px;
                font-size:0;
                background-color:transparent;
                position:absolute;
                top:20px;
                right:20px;
                &:before, &:after {
                    width:100%;
                    border-top:1px solid #222;
                    content:'';
                    position:absolute;
                    top:50%;
                    left:0;
                    display:block;
                }
                &:before {
                    transform:rotate(45deg);
                }
                &:after {
                    transform:rotate(-45deg);
                }
            }
        }
        .modal_footer {
            padding:20px;
            display:flex;
            justify-content:center;
            gap:10px;
        }
        .modal_content {
            max-height:100%;
            padding:30px;
            overflow:auto;
            flex:1;
            &::-webkit-scrollbar {
                width:10px;
            }
            &::-webkit-scrollbar-thumb {
                background:rgba(0,0,0,0.2);
                background-clip:padding-box;
                border:3px solid transparent;
                border-radius:8px;
            }
            &::-webkit-scrollbar-track {
                background:transparent;
            }
        }
    }
    &.full {
        .modal_container {
            width:calc(100% - 40px);

        }
    }
    &.alert {
        .modal_container {
            width:480px;
            .modal_footer {
                padding:0;
                gap:0;
                [class*='btn_'] {
                    height:60px;
                    font-size:16px;
                    flex:1;
                }
            }
        }
    }
    &.bottom {
        .modal_container {
            max-height:calc(100vh - 20px);
            top:initial;
            right:0;
            bottom:0;
            left:0;
            transform:initial;
            animation-duration: 0.25s;
            animation-name: show_up;
        }      
    }
}

@keyframes show_up {
    from {
        transform: translateY(100%);
    }
    to {
        transform: translateY(0);
    }
}

/* goods list */
.goods_list {
    margin-top:-40px;
    margin-left:-15px;
    display:flex;
    flex-wrap:wrap;
    > li {
        width:20%;
        padding-top:40px;
        padding-left:15px;
    }
}
